package Java.LearnProgramming;

public class A_DataTypes_Variables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num1=3;
		byte by =127;
		short sh = 56;
		long lg=3554l;
		
		double db = 5.67;
		float ft =4.5f;
		
		char ch = 'C';
		boolean b = true;
		
		System.out.println(num1 + " : " + by + " : " + sh + " : " + lg + " : "+ db + " : " +  ft + ":" + ch + " : " + b);
		
		
		//Literals
		
		int num2 = 0b101;
		int num3 = 0x7E;
		System.out.println(num2);
		System.out.println(num3);
		
		char cd = 'a';
		cd++;
		System.out.println();
		
	}

}
